from typing import Any, Dict

def tool_task_1(task_input: Dict[str, Any]) -> Dict[str, Any]:
    value = task_input.get("query") or task_input.get("doc") or task_input.get("text") or ""
    text = str(value).strip()
    if not text:
        text = "Create a workflow that runs a safe shell command only with explicit user confirmation and can be modified by user instruction"
    result = (
        "Subtask: Create a workflow that runs a safe shell command only with explicit user confirmation and can be modified by user instruction. "
        "Input: " + text + ". "
        "No verifier feedback."
    )
    return {
        "tool": "tool_task_1",
        "status": "ok",
        "result": result,
    }
